    int atid;
    private void list_atMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_list_atMouseClicked
        int selectedRow = list_at.getSelectedIndex();
        if (selectedRow != -1)
        {
            String atidStr = list_at.getSelectedValue();
            String numberOnly = atidStr.replaceAll("[^0-9]", ""); //format for int
            atid = Integer.parseInt(numberOnly);
            lbl_atid.setText("AT ID: " + atid);
            System.out.println("DEBUG: Clicked Record ID: " + atidStr);

            try
            {
                Connection conn = GlobalData.ConnectToDB("root", "password");
                ResultSet rs = DBUtilsAT.GetATRecordFromATID(conn, atid);

                //fill textfields
                if (rs.next())
                {
                    //txt_box.setText(rs.getString(sqlColumnName));
                    txt_atdate.setText(rs.getString("ATDate"));
                    txt_attime.setText(rs.getString("ATTime"));
                    txt_dectolerance.setText(rs.getString("DecreasedTolerance"));
                    txt_activity.setText(rs.getString("Activity"));
                    txt_actqty.setText(rs.getString("ActivityQty"));
                }
                conn.close();
            }
            catch(Exception e)
            {
                System.out.println(e.getMessage() );
            }
        }
    }//GEN-LAST:event_list_atMouseClicked

    private void btn_retrievallActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_retrievallActionPerformed
        try
        {
            int pid = GlobalData.patientID;
            lbl_patientname.setText("Current Patient: " + GlobalData.patientName);
            lbl_patientid.setText("Patient ID: " + GlobalData.patientID);

            Connection conn = GlobalData.ConnectToDB("root", "password");
            ResultSet rs = DBUtilsAT.GetATRecordsFromPID(conn, pid);

            DefaultListModel lst = new DefaultListModel();
            while(rs.next() )
            {
                String listData = "RECORD NUMBER: " + rs.getString(1);
                lst.addElement(listData);
            }
            list_at.setModel(lst);
            conn.close();
        }
        catch (Exception e)
        {
            System.out.println(e.getMessage() );
        }
    }//GEN-LAST:event_btn_retrievallActionPerformed

