    private void btn_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_saveActionPerformed
        try
        {
            Connection conn = GlobalData.ConnectToDB("root", "password");
            
            Integer decTolerance = Integer.parseInt(txt_dectolerance.getText());
            Integer actqty = Integer.parseInt(txt_actqty.getText());
            
            DBUtilsAT.UpdateATRecord(conn, atid, txt_atdate.getText(), txt_attime.getText(),
                txt_activity.getText(), decTolerance,  actqty);

            conn.close();
        }
        catch(Exception e)
        {
            System.out.println(e.getMessage() );
        }
    }//GEN-LAST:event_btn_saveActionPerformed

    private void btn_newActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_newActionPerformed

        try
        {
            int pid = GlobalData.patientID;
            Connection conn = GlobalData.ConnectToDB("root", "password");
            
            Integer decTolerance = Integer.parseInt(txt_dectolerance.getText());
            Integer actqty = Integer.parseInt(txt_actqty.getText());
            DBUtilsAT.InsertATRecord(conn, pid, txt_atdate.getText(), txt_attime.getText(), 
                    txt_activity.getText(), decTolerance, actqty);
            //maybe add a confirmation swing popup here

            //retrieve all after inserting
            ResultSet rs = DBUtilsAT.GetATRecordsFromPID(conn, pid);

            DefaultListModel lst = new DefaultListModel();
            while(rs.next() )
            {
                String listData = "RECORD NUMBER: " + rs.getString(1);
                lst.addElement(listData);
            }
            list_at.setModel(lst);
            conn.close();
        }
        catch(Exception e)
        {
            System.out.println(e.getMessage() );
        }
    }//GEN-LAST:event_btn_newActionPerformed

